import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Container, Table, Button } from "react-bootstrap";
import "bootstrap";

const dayjs = require("dayjs");
require("dayjs/locale/id");
dayjs.locale("id");

const IzinList = () => {
  const [izin, setIzin] = useState([]);

  useEffect(() => {
    getIzin();
  });

  const getIzin = async () => {
    const response = await axios.get("http://localhost:5000/izin");
    setIzin(response.data);
  };

  // method hapus izin
  const deleteIzin = async (izinId) => {
    await axios.delete(`http://localhost:5000/izin/${izinId}`);
    getIzin();
  };

  return (
    <Container fluid>
      <div class="column is-full p-5 is-mobile">
        <h1 className="title">Halaman Izin</h1>
        <h2 className="subtitle">Catatan Izin</h2>
      </div>
      <div class="box">
        <Link to="/Izin/add" className="button is-primary mb-2 is-small">
          Add New
        </Link>
        <div class="columns is-full is-centered is-mobile">
          <div class="column is-full p-5 is-mobile">
            <div class="table-container">
              <table class="table is-striped is-narrow is-hoverable is-fullwidth">
                <thead>
                  <tr>
                    <th class="has-text-centered">No</th>
                    <th class="has-text-centered">Nama</th>
                    <th class="has-text-centered">NIP</th>
                    <th class="has-text-centered">Jabatan</th>
                    <th class="has-text-centered">Keterangan</th>
                    <th class="has-text-centered">Tanggal Dibuat</th>
                    <th class="has-text-centered">Waktu Dibuat</th>
                    <th class="has-text-centered">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {izin.map((izin, index) => (
                    <tr key={izin.uuid}>
                      <td class="has-text-centered">{index + 1}</td>
                      <td class="has-text-centered">{izin.user.name}</td>
                      <td class="has-text-centered">{izin.user.nip}</td>
                      <td class="has-text-centered">{izin.user.jab}</td>
                      <td class="has-text-centered">{izin.name}</td>
                      <td class="has-text-centered">{`${dayjs(izin.createdAt).format("dddd, DD MMM YYYY")}`}</td>
                      <td class="has-text-centered">{`${dayjs(izin.createdAt).format("HH:mm")} WITA`}</td>
                      <td class="has-text-centered">
                        <Link to={`/izin/edit/${izin.uuid}`} className="button is-small is-info">
                          Edit
                        </Link>
                        <button onClick={() => deleteIzin(izin.uuid)} className="button is-small is-danger">
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </Container>
  );
};

export default IzinList;
