import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Container, Button, Card, Form } from "react-bootstrap";
import "bootstrap";

const FormAddIzin = () => {
  const [name, setName] = useState("");
  const [msg, setMsg] = useState("");
  const navigate = useNavigate();

  const saveIzin = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/izin", {
        name: name,
      });
      navigate("/izin");
    } catch (error) {
      if (error.response) {
        setMsg(error.response.data.msg);
      }
    }
  };
  return (
    <Container fluid>
      <div className="d-flex flex-column mt-2">
        <h2>Form Izin</h2>
      </div>
      <Card className="d-flex flex-column p-5" style={{ borderRadius: "1.2rem" }}>
        <Form onSubmit={saveIzin}>
          <p className="has-text-centered">{msg}</p>
          <div className="field">
            <label className="label">Keterangan Izin</label>
            <div className="control">
              <input type="text" className="input" value={name} onChange={(e) => setName(e.target.value)} placeholder="keterangan izin" />
            </div>
          </div>
          <div className="field">
            <div className="control">
              <Button type="submit" className="Button is-success">
                Save
              </Button>
            </div>
          </div>
        </Form>
      </Card>
    </Container>
  );
};

export default FormAddIzin;
