import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Container, Table, Button } from "react-bootstrap";
import "bootstrap";

const dayjs = require("dayjs");
require("dayjs/locale/id");
dayjs.locale("id");

const IzinList = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    const response = await axios.get("http://localhost:5000/users");
    setUsers(response.data);
  };

  function filterByStatus(user, status) {
    return user.filter(function (user) {
      return user.status.includes(status);
    });
  }
  const Tersedia = filterByStatus(users, "Tersedia");
  const Izin = filterByStatus(users, "Izin");

  console.log(Izin);

  return (
    <Container fluid>
      <hr></hr>
      <div></div>
      <Button>
        <Link to="/izin/add" style={{ color: "white", textDecoration: "none" }}>
          Izin Sekarang!
        </Link>{" "}
      </Button>
      <hr></hr>
      <div className="d-flex flex-row justify-content-around flex-wrap">
        <div className="mx-2">
          <div className="d-flex flex-column align-items-center">
            <h2>Sedang Di Kantor</h2>
          </div>
          <Table responsive hover>
            <thead>
              <tr>
                <th>No</th>
                <th>Nama</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {Tersedia.map((user, index) => (
                <tr key={user.uuid}>
                  <td>{index + 1}</td>
                  <td>{user.name}</td>
                  <td>{user.status}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>

        <div className="mx-2">
          <div className="d-flex flex-column align-items-center">
            <h2>Sedang Izin</h2>
          </div>
          <Table responsive hover>
            <thead>
              <tr>
                <th>No</th>
                <th>Nama</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {Izin.map((user, index) => (
                <tr key={user.uuid}>
                  <td>{index + 1}</td>
                  <td>{user.name}</td>
                  <td>{user.status}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      </div>
    </Container>
  );
};

export default IzinList;
