import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Container, Table } from "react-bootstrap";
import "bootstrap";

const dayjs = require("dayjs");
require("dayjs/locale/id");
dayjs.locale("id");

const IzinList = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    getUsers();
  });

  const updateUserStatus = async (userId) => {
    await axios.delete(`http://localhost:5000/users/${userId}/status`);
  };

  const getUsers = async () => {
    const response = await axios.get("http://localhost:5000/users");
    setUsers(response.data);
  };
  
  console.log(userId);
  function filterByStatus(user, status) {
    return user.filter(function (user) {
      return user.status.includes(status);
    });
  }
  const Tersedia = filterByStatus(users, "Tersedia");
  const Izin = filterByStatus(users, "Izin");

  return (
    <Container>
      <hr></hr>
      <div className="d-flex justify-content-center">
        <Link to="/izin/add" className="btn btn-primary" style={{ fontWeight: "700" }}>
          IZIN SEKARANG!
        </Link>{" "}
      </div>
      <hr></hr>
      <div className="d-flex flex-row justify-content-around flex-wrap">
        <div className="card m-3 p-3" style={{ width: "50rem" }}>
          <div className="d-flex flex-column align-items-center">
            <h2>Sedang Di Kantor</h2>
          </div>
          <Table responsive hover>
            <thead>
              <tr>
                <th>No</th>
                <th>Nama</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {Tersedia.map((user, index) => (
                <tr key={user.uuid}>
                  <td>{index + 1}</td>
                  <td>{user.name}</td>
                  <td>{user.status}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>

        <div className="m-3 p-3 card" style={{ width: "50rem" }}>
          <div className="d-flex flex-column align-items-center">
            <h2>Sedang Izin</h2>
          </div>
          <Table responsive hover>
            <thead>
              <tr>
                <th>No</th>
                <th>Nama</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {Izin.map((user, index) => (
                <tr key={user.uuid}>
                  <td>{index + 1}</td>
                  <td>{user.name}</td>
                  <td>{user.status}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      </div>
    </Container>
  );
};

export default IzinList;
