import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Container, Table, Button } from "react-bootstrap";
import "bootstrap";

const FormAddIzin = () => {
  const [name, setName] = useState("");
  const [msg, setMsg] = useState("");
  const navigate = useNavigate();

  const saveIzin = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/izin", {
        name: name,
      });
      navigate("/izin");
    } catch (error) {
      if (error.response) {
        setMsg(error.response.data.msg);
      }
    }
  };
  return (
    <Container fluid>
      <h2 className="subtitle">Tambahkan Izin</h2>
      <>
        <div className="card-content">
          <div className="content">
            <form onSubmit={saveIzin}>
              <p className="has-text-centered">{msg}</p>
              <div className="field">
                <label className="label">Keterangan Izin</label>
                <div className="control">
                  <input type="text" className="input" value={name} onChange={(e) => setName(e.target.value)} placeholder="keterangan izin" />
                </div>
              </div>
              <div className="field">
                <div className="control">
                  <button type="submit" className="button is-success">
                    Save
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </>
    </Container>
  );
};

export default FormAddIzin;
