import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Container, Table, Button, Col, Row } from "react-bootstrap";
import "bootstrap";

const UserList = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    const response = await axios.get("http://localhost:5000/users");
    setUsers(response.data);
  };

  // method hapus izin
  const deleteUser = async (userId) => {
    await axios.delete(`http://localhost:5000/users/${userId}`);
    getUsers();
  };

  return (
    <Container fluid>
      <div className="d-flex flex-column align-items-center mt-3">
        <h2>Daftar User</h2>
      </div>
      <div className="d-flex flex-column align-items-center mx-5 mb-2">
        <Link to="/users/add">Tambahkan</Link>
      </div>
      <div className="mx-5">
        {/* <Table responsive striped bordered hover>
          <thead>
            <tr>
              <th>No</th>
              <th>Nama</th>
              <th>NIP</th>
              <th>Jabatan</th>
              <th>Email</th>
              <th>Role</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={user.uuid}>
                <td>{index + 1}</td>
                <td>{user.name}</td>
                <td>{user.nip}</td>
                <td>{user.jab}</td>
                <td>{user.email}</td>
                <td>{user.role}</td>
                <td style={user.status === "Izin" ? { color: "red", fontWeight: "600" } : { color: "green", fontWeight: "600" }}>{user.status}</td>
                <td>
                  <Button variant="link" size="sm">
                    <Link to={`/users/edit/${user.uuid}`}>Ubah</Link>
                  </Button>
                  {"   "}
                  <Button variant="danger" size="sm" onClick={() => deleteUser(user.uuid)}>
                    Hapus
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table> */}
        <div className="d-flex flex-row flex-wrap justify-content-center align-items-center">
          {users.map((user, index) => (
            <Card>
              <div key={user.uuid} className="d-flex flex-row align-items-center m-2">
                <div className="p-1">
                  <div>Nama</div>
                  <div>Nip</div>
                  <div>Jabatan</div>
                  <div>Email</div>
                  <div>Role</div>
                  <div>Status</div>
                </div>
                <div className="p-1">
                  <div>{user.name}</div>
                  <div>{user.nip}</div>
                  <div>{user.jab}</div>
                  <div>{user.email}</div>
                  <div>{user.role}</div>
                  <div style={user.status === "Izin" ? { color: "red", fontWeight: "600" } : { color: "green", fontWeight: "600" }}>{user.status}</div>
                </div>
              </div>
              <div className="d-flex flex-row justify-content-center">
                <Button variant="primary" size="sm">
                  <Link to={`/users/edit/${user.uuid}`} style={{ color: "white", textDecoration: "none" }}>
                    Ubah
                  </Link>
                </Button>
                {"   "}
                <Button variant="danger" size="sm" onClick={() => deleteUser(user.uuid)}>
                  Hapus
                </Button>
              </div>
            </Card>
          ))}
        </div>
      </div>
    </Container>
  );
};

export default UserList;
