import React, { useState, useEffect } from "react";
import axios from "axios";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { Container, Table } from "react-bootstrap";
import "bootstrap";

const dayjs = require("dayjs");
require("dayjs/locale/id");
dayjs.locale("id");

const IzinList = () => {
  const [users, setUsers] = useState([]);
  const { user } = useSelector((state) => state.auth);
  const [izin, setIzin] = useState([]);
  const {} = useSelector((state) => state.auth);

  async function getUsers() {
    const response = await axios.get("http://localhost:5000/users");
    setUsers(response.data);
  }

  //filter kehadiran
  function filterByStatus(user, status) {
    return user.filter(function (user) {
      return user.status.includes(status);
    });
  }
  const Tersedia = filterByStatus(users, "Tersedia");
  const Izin = filterByStatus(users, "Izin");
  //

  const updateUserStatus = async (userId) => {
    await axios.patch(`http://localhost:5000/users/${userId}/status`);
    getUsers();
  };

  useEffect(() => {
    getUsers();
  }, []);

  const updateIzinStatus = async (izinId) => {
    await axios.patch(`http://localhost:5000/izin/${izinId}/status`);
    getUsers();
  };

  const getIzin = async () => {
    const response = await axios.get("http://localhost:5000/izin");
    setIzin(response.data);
  };

  function IzinByStatus(izin, status, us) {
    return izin.filter(function (izin) {
      return{
        
      }
      izin.status.includes(status);
      izin.user.name.includes(us);
    });
  }
  const belumSelesai = IzinByStatus(izin, "Belum", user);
  // const selesai = IzinByStatus(izin, "Selesai");

  useEffect(() => {
    getIzin();
  }, []);

  // console.log(selesai);
  console.log(user.name);
  return (
    <Container>
      <hr></hr>
      <div className="d-flex justify-content-center">
        {user && user.status === "Izin" ? (
          <Link to="/izin" onClick={() => updateUserStatus(user.uuid, () => updateIzinStatus(izin.uuid))} className="btn btn-success" style={{ fontWeight: "700" }}>
            Selesaikan Izin
          </Link>
        ) : (
          <Link to="/izin/add" className="btn btn-primary" style={{ fontWeight: "700" }}>
            Izin Sekarang
          </Link>
        )}
      </div>
      <hr></hr>
      <div className="d-flex flex-row justify-content-around flex-wrap">
        <div className="card m-3 p-3" style={{ width: "50rem" }}>
          <div className="d-flex flex-column align-items-center">
            <h2>Sedang Di Kantor</h2>
          </div>
          <Table responsive hover>
            <thead>
              <tr>
                <th>No</th>
                <th>Nama</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {Tersedia.map((user, index) => (
                <tr key={user.uuid}>
                  <td>{index + 1}</td>
                  <td>{user.name}</td>
                  <td>{user.status}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>

        <div className="m-3 p-3 card" style={{ width: "50rem" }}>
          <div className="d-flex flex-column align-items-center">
            <h2>Sedang Izin</h2>
          </div>
          <Table responsive hover>
            <thead>
              <tr>
                <th>No</th>
                <th>Nama</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {Izin.map((user, index) => (
                <tr key={user.uuid}>
                  <td>{index + 1}</td>
                  <td>{user.name}</td>
                  <td>{user.status}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>

        <div className="m-3 p-3 card" style={{ width: "50rem" }}>
          <div className="d-flex flex-column align-items-center">
            <h2>Riwayat Izin</h2>
          </div>
          <Table responsive hover>
            <thead>
              <tr>
                <th>No</th>
                <th>Nama</th>
                <th>Ket</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {belumSelesai.map((izin, index) => (
                <tr key={izin.uuid}>
                  <td>{index + 1}</td>
                  <td>{izin.user.name}</td>
                  <td>{izin.ket}</td>
                  <td>{izin.status}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      </div>
    </Container>
  );
};

export default IzinList;
