    <Container fluid>
      <div className="d-flex flex-column align-items-center">
        <h2>Daftar User</h2>
      </div>
      <div className="d-flex flex-column align-items-end mx-5 mb-2">
        <Link to="/users/add">Tambahkan</Link>
      </div>
      <div className="mx-5">
        <Table responsive striped bordered hover>
          <thead>
            <tr>
              <th>No</th>
              <th>Nama</th>
              <th>NIP</th>
              <th>Jabatan</th>
              <th>Keterangan</th>
